var body = document.querySelector('body')
var menuButton = document.querySelector('#overlay-button')
var aboutButton = document.querySelector('.triangle-bottom')
var lightGallery = window.lightGallery

// Mobile menu
function toggleOverlay () {
  body.classList.toggle('menu-active')
  document.querySelector('html').classList.toggle('fixed')
}

// Front page menu
if (menuButton) {
  menuButton.addEventListener('click', toggleOverlay)
}

// Portfolio grid
if (aboutButton) {
  aboutButton.addEventListener('click', toggleOverlay)
}

// Portfolio home page images
if (lightGallery && document.querySelector('.subportfolio-grid')) {
  lightGallery(document.querySelector('.subportfolio-grid'), {
    subHtmlSelectorRelative: true,
    speed: 600,
    counter: false,
    showAfterLoad: true,
    download: false,
    cssEasing: 'ease-in-out',
    swipeThreshold: 25,
    mode: 'lg-slide',
    hideBarsDelay: 99999999,
    youtubePlayerParams: {
      autoplay: 0
    },
    preload: 0,
    autoplay: false
  })
}

// On pages with main images on the right
if (lightGallery && document.querySelector('.main-images, .main-image')) {
  lightGallery(document.querySelector('.main-images, .main-image'), {
    subHtmlSelectorRelative: true,
    speed: 600,
    counter: false,
    showAfterLoad: true,
    download: false,
    cssEasing: 'ease-in-out',
    swipeThreshold: 25,
    mode: 'lg-slide',
    hideBarsDelay: 99999999,
    youtubePlayerParams: {
      autoplay: 0
    },
    preload: 0,
    autoplay: false
  })
}
